import React, {useState} from 'react';
import { Plus, Minus } from 'lucide-react';
//I need to import useState because its a special hook from React that let's me manage states etc,
// I also need to import the lucide-react library to use the icons

const CounterApp = () => {
  const [count, setCount] = useState(0)
  //I'm using the useState hook, which can return a state variable and a setter function. 
  const increase = () => {
    setCount(count + 1);
  };
  //Creating a function that increases count by one when called.

  const decrease = () => {
    setCount(count - 1);
  };
  //Creating a function that decreases count by one when called.
  return (
    <div className="flex items-center">
      <p className="text-2xl">Hello, this is my first react app.
      It is a counter... in case you forgot how to count or if you need to count more than the number of fingers you have.</p>
      <div></div>
      <button 
      onClick={decrease}
      className="flex p-2" 
      style={{ backgroundColor: '#EEE7DD' }}><Minus size={24} color='#593E21'/></button>
      <span>{count}</span>
      <button 
      onClick={increase}
      className="flex p-2" 
      style={{ backgroundColor: '#EEE7DD' }}><Plus size={24} color='#593E21'/></button>
    </div>
  )
}

export default CounterApp;
